mapper.select:
SELECT p.*, u.user_name FROM TB_POST p JOIN TB_USER u ON p.user_no = u.user_no
p.IS_POST_DELETE = 'N' AND u.exit_status = 'N'
AND p.board_no = #{boardNo}
AND p.POST_TITLE LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY p.POST_REG_DATE DESC LIMIT #{limitPageNo}, #{numPerPage}

SELECT p.*, u.user_name FROM TB_POST p JOIN TB_USER u ON p.user_no = u.user_no
p.IS_POST_DELETE = 'N' AND u.exit_status = 'N'
AND p.board_no = #{boardNo}
AND p.POST_TITLE LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY p.POST_REG_DATE DESC LIMIT #{offset}, #{numPerPage}

mapper.insert:
INSERT INTO TB_POST (
  post_no, user_no, board_no, post_title, post_content, comment_status, post_reg_date
) VALUES (
  #{postNo}, #{userNo}, #{boardNo}, #{postTitle}, #{postContent}, #{commentStatus}, NOW()
)

mapper.update:
UPDATE TB_POST
SET VIEW_CNT = VIEW_CNT + 1
WHERE POST_NO = #{postNo}
