mapper.update:
UPDATE TB_USER 
SET exit_status = 'Y', exit_date = NOW() 
WHERE USER_ID = #{userId}

mapper.select:
SELECT * FROM TB_USER WHERE exit_status = 'N'
AND user_name LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY user_reg_date DESC LIMIT #{start}, #{end}

mapper.update:
UPDATE TB_USER
SET FAIL_CNT = FAIL_CNT + 1
WHERE USER_ID = #{userId}

UPDATE TB_USER
SET ACCOUNT_STATUS = 'Y'
WHERE USER_ID = #{userId}

